import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';
import { sampleObjects } from './sampleData';

const ImprovedARVisualizer = () => {
  const mountRef = useRef(null);
  const videoRef = useRef(null);
  const [objects, setObjects] = useState([]);
  const [selectedObject, setSelectedObject] = useState(null);
  const [showDataPanel, setShowDataPanel] = useState(false);
  const [cameraActive, setCameraActive] = useState(false);
  const [videoStream, setVideoStream] = useState(null);
  const [deviceOrientation, setDeviceOrientation] = useState({ alpha: 0, beta: 0, gamma: 0 });
  const [deviceMotion, setDeviceMotion] = useState({ x: 0, y: 0, z: 0 });
  
  // Request camera access immediately on component mount
  useEffect(() => {
    requestCameraAccess();
    
    // Request permission for device orientation if on iOS 13+
    if (typeof DeviceOrientationEvent !== 'undefined' && 
        typeof DeviceOrientationEvent.requestPermission === 'function') {
      // iOS 13+ requires explicit permission
      DeviceOrientationEvent.requestPermission()
        .then(response => {
          if (response === 'granted') {
            window.addEventListener('deviceorientation', handleOrientation);
            window.addEventListener('devicemotion', handleMotion);
          }
        })
        .catch(console.error);
    } else {
      // Non iOS 13+ devices
      window.addEventListener('deviceorientation', handleOrientation);
      window.addEventListener('devicemotion', handleMotion);
    }
    
    return () => {
      window.removeEventListener('deviceorientation', handleOrientation);
      window.removeEventListener('devicemotion', handleMotion);
    };
  }, []);
  
  // Handle device orientation changes
  const handleOrientation = (event) => {
    setDeviceOrientation({
      alpha: event.alpha || 0, // Z-axis rotation [0, 360)
      beta: event.beta || 0,   // X-axis rotation [-180, 180)
      gamma: event.gamma || 0  // Y-axis rotation [-90, 90)
    });
  };
  
  // Handle device motion
  const handleMotion = (event) => {
    const acceleration = event.accelerationIncludingGravity;
    if (acceleration) {
      setDeviceMotion({
        x: acceleration.x || 0,
        y: acceleration.y || 0,
        z: acceleration.z || 0
      });
    }
  };
  
  // Request camera access
  const requestCameraAccess = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: "environment",
          width: { ideal: window.innerWidth },
          height: { ideal: window.innerHeight }
        } 
      });
      
      setVideoStream(stream);
      setCameraActive(true);
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (error) {
      console.error("Error accessing camera:", error);
      alert("Could not access the camera. Please check permissions.");
    }
  };
  
  // Stop camera stream
  const stopCameraStream = () => {
    if (videoStream) {
      videoStream.getTracks().forEach(track => track.stop());
      setVideoStream(null);
      setCameraActive(false);
    }
  };
  
  // Scene setup
  useEffect(() => {
    if (!cameraActive) return;
    
    // Scene, camera, and renderer setup
    const scene = new THREE.Scene();
    
    const camera = new THREE.PerspectiveCamera(
      75, 
      window.innerWidth / window.innerHeight, 
      0.1, 
      1000
    );
    camera.position.set(0, 0, 5); // Start position
    
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      alpha: true // Transparent background to see camera feed
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setClearColor(0x000000, 0); // Transparent background
    
    // Add renderer to DOM
    mountRef.current.appendChild(renderer.domElement);
    
    // Lighting
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambientLight);
    
    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(1, 1, 1);
    scene.add(directionalLight);
    
    // Subtle grid for depth perception
    const gridHelper = new THREE.GridHelper(20, 20, 0x555555, 0x333333);
    gridHelper.position.y = -2; // Place grid below eye level
    gridHelper.material.opacity = 0.4;
    gridHelper.material.transparent = true;
    scene.add(gridHelper);
    
    // Create fixed AR objects in the scene
    const objectsInScene = [];
    
    sampleObjects.forEach(objectData => {
      // Create marker mesh with higher quality
      const geometry = new THREE.SphereGeometry(0.2, 32, 32);
      const material = new THREE.MeshStandardMaterial({ 
        color: objectData.color,
        emissive: objectData.color,
        emissiveIntensity: 0.4,
        roughness: 0.3,
        metalness: 0.8
      });
      const mesh = new THREE.Mesh(geometry, material);
      
      // Position in a fixed location around the user
      // Distribute objects in a circle around the camera
      const angle = Math.random() * Math.PI * 2;
      const radius = 3 + Math.random() * 2; // Random distance between 3-5 units
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;
      const y = (Math.random() - 0.5) * 2; // Random height
      
      mesh.position.set(x, y, z);
      
      // Add enhanced pulse effect
      const pulseGeometry = new THREE.SphereGeometry(0.25, 32, 32);
      const pulseMaterial = new THREE.MeshBasicMaterial({
        color: objectData.color,
        transparent: true,
        opacity: 0.4
      });
      const pulse = new THREE.Mesh(pulseGeometry, pulseMaterial);
      pulse.scale.set(1, 1, 1);
      
      // Set pulse properties based on status
      let pulseSpeed = 0.01;
      let maxScale = 1.5;
      
      switch(objectData.status) {
        case "Critical":
          pulseSpeed = 0.03;
          maxScale = 2.0;
          break;
        case "Warning":
          pulseSpeed = 0.02;
          maxScale = 1.8;
          break;
        case "Offline":
          pulseSpeed = 0.005;
          maxScale = 1.2;
          break;
        default: // Active
          pulseSpeed = 0.01;
          maxScale = 1.5;
      }
      
      pulse.userData = { 
        pulseDirection: 1, 
        minScale: 1, 
        maxScale: maxScale,
        pulseSpeed: pulseSpeed
      };
      mesh.add(pulse);
      
      // Add floating status indicator
      const statusGeometry = new THREE.BoxGeometry(0.05, 0.05, 0.05);
      const statusColor = getStatusColor(objectData.status);
      const statusMaterial = new THREE.MeshBasicMaterial({ color: statusColor });
      const statusIndicator = new THREE.Mesh(statusGeometry, statusMaterial);
      statusIndicator.position.set(0, 0.3, 0);
      mesh.add(statusIndicator);
      
      // Add label
      const labelDiv = document.createElement('div');
      labelDiv.className = 'object-label';
      labelDiv.textContent = objectData.name;
      labelDiv.style.display = 'none';
      
      // Create a text sprite for the 3D label
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      canvas.width = 256;
      canvas.height = 128;
      
      context.fillStyle = 'rgba(25, 28, 36, 0.85)';
      context.strokeStyle = 'rgba(255, 255, 255, 0.2)';
      context.lineWidth = 2;
      
      // Draw rounded rectangle
      const rectWidth = 210;
      const rectHeight = 50;
      const cornerRadius = 8;
      
      context.beginPath();
      context.moveTo(20 + cornerRadius, 10);
      context.lineTo(20 + rectWidth - cornerRadius, 10);
      context.quadraticCurveTo(20 + rectWidth, 10, 20 + rectWidth, 10 + cornerRadius);
      context.lineTo(20 + rectWidth, 10 + rectHeight - cornerRadius);
      context.quadraticCurveTo(20 + rectWidth, 10 + rectHeight, 20 + rectWidth - cornerRadius, 10 + rectHeight);
      context.lineTo(20 + cornerRadius, 10 + rectHeight);
      context.quadraticCurveTo(20, 10 + rectHeight, 20, 10 + rectHeight - cornerRadius);
      context.lineTo(20, 10 + cornerRadius);
      context.quadraticCurveTo(20, 10, 20 + cornerRadius, 10);
      context.closePath();
      
      context.fill();
      context.stroke();
      
      // Add text
      context.font = 'bold 20px Arial';
      context.fillStyle = 'white';
      context.textAlign = 'center';
      context.textBaseline = 'middle';
      context.fillText(objectData.name, canvas.width / 2, 35);
      
      // Create sprite
      const texture = new THREE.CanvasTexture(canvas);
      const spriteMaterial = new THREE.SpriteMaterial({ 
        map: texture,
        transparent: true
      });
      const sprite = new THREE.Sprite(spriteMaterial);
      sprite.scale.set(1.0, 0.5, 1);
      sprite.position.set(0, 0.5, 0);
      sprite.visible = false; // Hidden by default
      mesh.add(sprite);
      
      // Store the object with its metadata
      const objectWithMeta = {
        mesh,
        data: objectData,
        label: labelDiv,
        sprite
      };
      
      scene.add(mesh);
      objectsInScene.push(objectWithMeta);
    });
    
    setObjects(objectsInScene);
    
    // Helper function to get status color
    function getStatusColor(status) {
      switch(status) {
        case "Critical": return 0xff0000;
        case "Warning": return 0xffcc00;
        case "Offline": return 0x888888;
        default: return 0x00ff00; // Active
      }
    }
    
    // Raycaster for object selection
    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2();
    
    // Handle tap/click
    const handleTap = (event) => {
      // Normalize mouse/touch coordinates
      const x = (event.clientX / window.innerWidth) * 2 - 1;
      const y = -(event.clientY / window.innerHeight) * 2 + 1;
      mouse.set(x, y);
      
      // Update the raycaster
      raycaster.setFromCamera(mouse, camera);
      
      // Find intersections
      const meshes = objectsInScene.map(obj => obj.mesh);
      const intersects = raycaster.intersectObjects(meshes, true);
      
      // If we intersected with something
      if (intersects.length > 0) {
        // Get the root parent object (in case we hit a child like pulse)
        let selectedMesh = intersects[0].object;
        while (selectedMesh.parent && selectedMesh.parent !== scene) {
          selectedMesh = selectedMesh.parent;
        }
        
        const selectedObj = objectsInScene.find(obj => obj.mesh === selectedMesh);
        
        if (selectedObj) {
          if (selectedObject === selectedObj) {
            // Toggle data panel if tapping the same object
            setShowDataPanel(!showDataPanel);
          } else {
            // Select new object and show data panel
            setSelectedObject(selectedObj);
            setShowDataPanel(true);
          }
          
          // Hide all sprites
          objectsInScene.forEach(obj => {
            if (obj.sprite) obj.sprite.visible = false;
          });
          
          // Show sprite for selected object
          selectedObj.sprite.visible = true;
        }
      } else {
        // Hide data panel but keep selection
        setShowDataPanel(false);
        
        // Hide all sprite labels when tapping on empty space
        objectsInScene.forEach(obj => {
          if (obj.sprite) obj.sprite.visible = false;
        });
      }
    };
    
    // Handle device orientation to update camera view
    const updateCameraFromOrientation = () => {
      // This is a simplified calculation and might need calibration
      // depending on device and desired behavior
      
      // Only apply rotation if we have significant movement
      if (deviceOrientation.alpha !== 0 || deviceOrientation.beta !== 0 || deviceOrientation.gamma !== 0) {
        // Create a rotation matrix from device orientation
        const euler = new THREE.Euler(
          THREE.MathUtils.degToRad(deviceOrientation.beta), 
          THREE.MathUtils.degToRad(deviceOrientation.alpha), 
          -THREE.MathUtils.degToRad(deviceOrientation.gamma),
          'YXZ' // Order matters for rotation
        );
        
        camera.setRotationFromEuler(euler);
      }
    };
    
    // Setup event listeners
    window.addEventListener('click', handleTap);
    window.addEventListener('touchend', (e) => handleTap(e.changedTouches[0]));
    
    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    
    window.addEventListener('resize', handleResize);
    
    // Animation loop
    const animate = () => {
      const animationId = requestAnimationFrame(animate);
      
      // Update camera based on device orientation
      updateCameraFromOrientation();
      
      // Update pulse animations
      objectsInScene.forEach(obj => {
        const pulse = obj.mesh.children[0];
        
        // Get pulse speed from userData
        const pulseSpeed = pulse.userData.pulseSpeed;
        
        // Update pulse scale
        if (pulse.userData.pulseDirection === 1) {
          pulse.scale.x += pulseSpeed;
          pulse.scale.y += pulseSpeed;
          pulse.scale.z += pulseSpeed;
          
          if (pulse.scale.x >= pulse.userData.maxScale) {
            pulse.userData.pulseDirection = -1;
          }
        } else {
          pulse.scale.x -= pulseSpeed;
          pulse.scale.y -= pulseSpeed;
          pulse.scale.z -= pulseSpeed;
          
          if (pulse.scale.x <= pulse.userData.minScale) {
            pulse.userData.pulseDirection = 1;
          }
        }
        
        // Update opacity based on scale
        pulse.material.opacity = 0.5 - ((pulse.scale.x - pulse.userData.minScale) / 
          (pulse.userData.maxScale - pulse.userData.minScale)) * 0.4;
          
        // Rotate status indicators slightly
        if (obj.mesh.children.length > 1) {
          obj.mesh.children[1].rotation.y += 0.01;
        }
        
        // Make sprite labels always face camera
        if (obj.sprite) {
          obj.sprite.lookAt(camera.position);
        }
      });
      
      renderer.render(scene, camera);
    };
    
    const animationId = requestAnimationFrame(animate);
    
    // Add HTML overlays to the DOM
    objectsInScene.forEach(obj => {
      document.body.appendChild(obj.label);
    });
    
    // Cleanup function
    return () => {
      window.removeEventListener('click', handleTap);
      window.removeEventListener('touchend', handleTap);
      window.removeEventListener('resize', handleResize);
      
      cancelAnimationFrame(animationId);
      
      objectsInScene.forEach(obj => {
        if (obj.label.parentNode) {
          obj.label.parentNode.removeChild(obj.label);
        }
      });
      
      if (mountRef.current && mountRef.current.contains(renderer.domElement)) {
        mountRef.current.removeChild(renderer.domElement);
      }
    };
  }, [cameraActive, deviceOrientation, selectedObject]);
  
  // Format date string nicely
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };
  
  // Get status indicator color
  const getStatusClass = (status) => {
    switch(status) {
      case "Critical": return "status-critical";
      case "Warning": return "status-warning";
      case "Offline": return "status-offline";
      default: return "status-active";
    }
  };
  
  // Render enhanced card-style data panel
  const renderDataPanel = () => {
    if (!selectedObject || !showDataPanel) return null;
    
    const { data } = selectedObject;
    const statusClass = getStatusClass(data.status);
    
    return (
      <div className="data-panel card-ui">
        <div className="panel-header" style={{ backgroundColor: `#${data.color.toString(16)}` }}>
          <div className="header-content">
            <div className={`status-indicator ${statusClass}`}></div>
            <h3>{data.name}</h3>
          </div>
          <button className="close-button" onClick={() => setShowDataPanel(false)}></button>
        </div>
        
        <div className="panel-content">
          <div className="data-grid">
            <div className="data-section location-section">
              <h4>Location</h4>
              <div className="data-grid-row">
                <div className="data-grid-cell">
                  <span className="data-label">X:</span>
                  <span className="data-value">{selectedObject.mesh.position.x.toFixed(2)} m</span>
                </div>
                <div className="data-grid-cell">
                  <span className="data-label">Y:</span>
                  <span className="data-value">{selectedObject.mesh.position.y.toFixed(2)} m</span>
                </div>
                <div className="data-grid-cell">
                  <span className="data-label">Z:</span>
                  <span className="data-value">{selectedObject.mesh.position.z.toFixed(2)} m</span>
                </div>
              </div>
            </div>
            
            <div className="data-section properties-section">
              <h4>Properties</h4>
              <div className="data-grid-row">
                <div className="data-grid-cell">
                  <span className="data-label">Type:</span>
                  <span className="data-value">{data.type}</span>
                </div>
                <div className="data-grid-cell">
                  <span className="data-label">Status:</span>
                  <span className={`data-value ${statusClass}`}>{data.status}</span>
                </div>
              </div>
              <div className="data-grid-row">
                <div className="data-grid-cell full-width">
                  <span className="data-label">Last Updated:</span>
                  <span className="data-value">{formatDate(data.lastUpdated)}</span>
                </div>
              </div>
              <div className="data-grid-row">
                <div className="data-grid-cell full-width">
                  <span className="data-label">Description:</span>
                  <span className="data-value description">{data.description}</span>
                </div>
              </div>
            </div>
            
            <div className="data-section readings-section">
              <h4>Recent Readings</h4>
              <div className="mini-chart">
                {data.readings.map((value, index) => (
                  <div 
                    key={index} 
                    className="chart-bar"
                    style={{ 
                      height: `${value}%`,
                      backgroundColor: index === data.readings.length - 1 ? 
                        `#${data.color.toString(16)}` : '#3498db'
                    }}
                  >
                    <span className="tooltip">{value}</span>
                  </div>
                ))}
              </div>
              <div className="chart-legend">
                <span>Past</span>
                <span>Present</span>
              </div>
            </div>
          </div>
          
          <div className="action-buttons">
            <button className="action-button">
              <i className="icon-details"></i>
              <span>Details</span>
            </button>
            <button className="action-button">
              <i className="icon-history"></i>
              <span>History</span>
            </button>
            <button className="action-button">
              <i className="icon-share"></i>
              <span>Share</span>
            </button>
          </div>
        </div>
      </div>
    );
  };
  
  // Render AR mode instructions
  const renderInstructions = () => {
    return (
      <div className="instructions card-ui">
        <p>Look around to find virtual objects. Tap objects to view details.</p>
        <button className="dismiss-button"></button>
      </div>
    );
  };
  
  // Render simple status bar
  const renderStatusBar = () => {
    const activeObjects = objects.filter(obj => obj.data.status === "Active").length;
    const warningObjects = objects.filter(obj => obj.data.status === "Warning").length;
    const criticalObjects = objects.filter(obj => obj.data.status === "Critical").length;
    
    return (
      <div className="status-bar card-ui">
        <div className="status-content">
          <div className="object-count">
            <span className="count-number">{objects.length}</span>
            <span className="count-label">Objects</span>
          </div>
          <div className="status-indicators">
            <div className="status-group">
              <div className="status-dot status-active"></div>
              <span>{activeObjects}</span>
            </div>
            <div className="status-group">
              <div className="status-dot status-warning"></div>
              <span>{warningObjects}</span>
            </div>
            <div className="status-group">
              <div className="status-dot status-critical"></div>
              <span>{criticalObjects}</span>
            </div>
          </div>
        </div>
      </div>
    );
  };
  
  return (
    <div className="ar-container">
      {/* Camera video feed for AR background */}
      <video 
        ref={videoRef}
        className="camera-feed"
        autoPlay
        playsInline
        muted
      />
      
      {/* Three.js scene */}
      <div ref={mountRef} className="ar-scene"></div>
      
      {/* UI Components */}
      <div className="ui-layer">
        {renderDataPanel()}
        {renderStatusBar()}
        {renderInstructions()}
        
        {/* Camera permissions info */}
        {!cameraActive && (
          <div className="camera-permissions card-ui">
            <h3>Camera Access Required</h3>
            <p>This AR application needs access to your camera to function.</p>
            <button 
              className="permission-button"
              onClick={requestCameraAccess}
            >
              Grant Camera Access
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ImprovedARVisualizer;
