import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { sampleObjects } from './sampleData';

const ImprovedARVisualizer = () => {
  const mountRef = useRef(null);
  const [objects, setObjects] = useState([]);
  const [selectedObject, setSelectedObject] = useState(null);
  const [showDataPanel, setShowDataPanel] = useState(false);
  const [cameraActive, setCameraActive] = useState(false);
  const [videoStream, setVideoStream] = useState(null);
  const videoRef = useRef(null);
  
  // Request camera access
  const requestCameraAccess = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: "environment" } 
      });
      setVideoStream(stream);
      setCameraActive(true);
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (error) {
      console.error("Error accessing camera:", error);
      alert("Could not access the camera. Please check permissions.");
    }
  };
  
  // Stop camera stream
  const stopCameraStream = () => {
    if (videoStream) {
      videoStream.getTracks().forEach(track => track.stop());
      setVideoStream(null);
      setCameraActive(false);
    }
  };
  
  // Toggle camera
  const toggleCamera = () => {
    if (cameraActive) {
      stopCameraStream();
    } else {
      requestCameraAccess();
    }
  };
  
  // Scene setup
  useEffect(() => {
    // Scene, camera, and renderer
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x111111);
    
    const camera = new THREE.PerspectiveCamera(
      75, 
      window.innerWidth / window.innerHeight, 
      0.1, 
      1000
    );
    camera.position.z = 5;
    
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      alpha: true // Enable transparency for AR effect
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setClearColor(0x000000, 0); // Transparent background
    mountRef.current.appendChild(renderer.domElement);
    
    // Enhanced controls
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.rotateSpeed = 0.5;
    controls.zoomSpeed = 1.2;
    controls.panSpeed = 0.8;
    controls.minDistance = 2;
    controls.maxDistance = 20;
    
    // Lighting
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambientLight);
    
    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(1, 1, 1);
    scene.add(directionalLight);
    
    // Grid helper with better visibility
    const gridHelper = new THREE.GridHelper(20, 20, 0x555555, 0x333333);
    gridHelper.material.opacity = 0.6;
    gridHelper.material.transparent = true;
    scene.add(gridHelper);
    
    // Create 3D objects
    const objectsInScene = [];
    
    sampleObjects.forEach(objectData => {
      // Create marker mesh with higher quality
      const geometry = new THREE.SphereGeometry(0.2, 32, 32);
      const material = new THREE.MeshStandardMaterial({ 
        color: objectData.color,
        emissive: objectData.color,
        emissiveIntensity: 0.4,
        roughness: 0.3,
        metalness: 0.8
      });
      const mesh = new THREE.Mesh(geometry, material);
      
      // Position based on data
      mesh.position.set(
        objectData.position.x,
        objectData.position.y,
        objectData.position.z
      );
      
      // Add enhanced pulse effect
      const pulseGeometry = new THREE.SphereGeometry(0.25, 32, 32);
      const pulseMaterial = new THREE.MeshBasicMaterial({
        color: objectData.color,
        transparent: true,
        opacity: 0.4
      });
      const pulse = new THREE.Mesh(pulseGeometry, pulseMaterial);
      pulse.scale.set(1, 1, 1);
      
      // Set pulse properties based on status
      let pulseSpeed = 0.01;
      let maxScale = 1.5;
      
      switch(objectData.status) {
        case "Critical":
          pulseSpeed = 0.03;
          maxScale = 2.0;
          break;
        case "Warning":
          pulseSpeed = 0.02;
          maxScale = 1.8;
          break;
        case "Offline":
          pulseSpeed = 0.005;
          maxScale = 1.2;
          break;
        default: // Active
          pulseSpeed = 0.01;
          maxScale = 1.5;
      }
      
      pulse.userData = { 
        pulseDirection: 1, 
        minScale: 1, 
        maxScale: maxScale,
        pulseSpeed: pulseSpeed
      };
      mesh.add(pulse);
      
      // Add floating status indicator
      const statusGeometry = new THREE.BoxGeometry(0.05, 0.05, 0.05);
      const statusColor = getStatusColor(objectData.status);
      const statusMaterial = new THREE.MeshBasicMaterial({ color: statusColor });
      const statusIndicator = new THREE.Mesh(statusGeometry, statusMaterial);
      statusIndicator.position.set(0, 0.3, 0);
      mesh.add(statusIndicator);
      
      // Add label
      const labelDiv = document.createElement('div');
      labelDiv.className = 'object-label';
      labelDiv.textContent = objectData.name;
      labelDiv.style.display = 'none';
      
      // Store the object with its metadata
      const objectWithMeta = {
        mesh,
        data: objectData,
        label: labelDiv
      };
      
      scene.add(mesh);
      objectsInScene.push(objectWithMeta);
    });
    
    setObjects(objectsInScene);
    
    // Helper function to get status color
    function getStatusColor(status) {
      switch(status) {
        case "Critical": return 0xff0000;
        case "Warning": return 0xffcc00;
        case "Offline": return 0x888888;
        default: return 0x00ff00; // Active
      }
    }
    
    // Raycaster for object selection
    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2();
    
    // Handle mouse click
    const handleMouseClick = (event) => {
      // Calculate mouse position in normalized device coordinates
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
      
      // Update the raycaster
      raycaster.setFromCamera(mouse, camera);
      
      // Find intersections
      const meshes = objectsInScene.map(obj => obj.mesh);
      const intersects = raycaster.intersectObjects(meshes, true);
      
      // If we intersected with something
      if (intersects.length > 0) {
        // Get the root parent object (in case we hit a child like pulse)
        let selectedMesh = intersects[0].object;
        while (selectedMesh.parent && selectedMesh.parent !== scene) {
          selectedMesh = selectedMesh.parent;
        }
        
        const selectedObj = objectsInScene.find(obj => obj.mesh === selectedMesh);
        
        // Set the selected object
        setSelectedObject(selectedObj);
        setShowDataPanel(true);
        
        // Update visuals for the selected object
        objectsInScene.forEach(obj => {
          if (obj.mesh === selectedMesh) {
            // Highlight the selected object
            obj.mesh.scale.set(1.3, 1.3, 1.3);
            
            // Make pulse more prominent
            const pulse = obj.mesh.children[0];
            pulse.userData.minScale = 1.2;
            pulse.userData.maxScale = 2.5;
            pulse.userData.pulseSpeed = 0.02;
          } else {
            // Reset others
            obj.mesh.scale.set(1, 1, 1);
            
            // Reset pulse
            const pulse = obj.mesh.children[0];
            pulse.userData.minScale = 1;
            pulse.userData.maxScale = 1.5;
            pulse.userData.pulseSpeed = 0.01;
          }
        });
      } else {
        // If clicking on empty space, deselect
        setSelectedObject(null);
        setShowDataPanel(false);
        
        // Reset all objects
        objectsInScene.forEach(obj => {
          obj.mesh.scale.set(1, 1, 1);
          
          // Reset pulse
          const pulse = obj.mesh.children[0];
          pulse.userData.minScale = 1;
          pulse.userData.maxScale = 1.5;
          pulse.userData.pulseSpeed = 0.01;
        });
      }
    };
    
    // Handle mouse move for hover effects
    const handleMouseMove = (event) => {
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
      
      raycaster.setFromCamera(mouse, camera);
      const meshes = objectsInScene.map(obj => obj.mesh);
      const intersects = raycaster.intersectObjects(meshes, true);
      
      // Show/hide labels based on hover
      objectsInScene.forEach(obj => {
        // Hide all labels by default
        obj.label.style.display = 'none';
        obj.mesh.material.emissiveIntensity = 0.4;
      });
      
      // Show label for hovered object
      if (intersects.length > 0) {
        // Get the root parent object (in case we hit a child like pulse)
        let hoveredMesh = intersects[0].object;
        while (hoveredMesh.parent && hoveredMesh.parent !== scene) {
          hoveredMesh = hoveredMesh.parent;
        }
        
        const hoveredObj = objectsInScene.find(obj => obj.mesh === hoveredMesh);
        
        if (hoveredObj) {
          // Position and show the label
          const vector = new THREE.Vector3();
          vector.setFromMatrixPosition(hoveredObj.mesh.matrixWorld);
          vector.project(camera);
          
          const x = (vector.x * 0.5 + 0.5) * window.innerWidth;
          const y = (-(vector.y * 0.5) + 0.5) * window.innerHeight;
          
          hoveredObj.label.style.display = 'block';
          hoveredObj.label.style.left = `${x}px`;
          hoveredObj.label.style.top = `${y - 30}px`;
          
          // Enhance glow effect
          hoveredObj.mesh.material.emissiveIntensity = 0.8;
          document.body.style.cursor = 'pointer';
        }
      } else {
        document.body.style.cursor = 'default';
      }
    };
    
    window.addEventListener('click', handleMouseClick);
    window.addEventListener('mousemove', handleMouseMove);
    
    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      
      // Update pulse animations
      objectsInScene.forEach(obj => {
        const pulse = obj.mesh.children[0];
        
        // Get pulse speed from userData
        const pulseSpeed = pulse.userData.pulseSpeed;
        
        // Update pulse scale
        if (pulse.userData.pulseDirection === 1) {
          pulse.scale.x += pulseSpeed;
          pulse.scale.y += pulseSpeed;
          pulse.scale.z += pulseSpeed;
          
          if (pulse.scale.x >= pulse.userData.maxScale) {
            pulse.userData.pulseDirection = -1;
          }
        } else {
          pulse.scale.x -= pulseSpeed;
          pulse.scale.y -= pulseSpeed;
          pulse.scale.z -= pulseSpeed;
          
          if (pulse.scale.x <= pulse.userData.minScale) {
            pulse.userData.pulseDirection = 1;
          }
        }
        
        // Update opacity based on scale
        pulse.material.opacity = 0.5 - ((pulse.scale.x - pulse.userData.minScale) / 
          (pulse.userData.maxScale - pulse.userData.minScale)) * 0.4;
          
        // Rotate status indicators slightly
        if (obj.mesh.children.length > 1) {
          obj.mesh.children[1].rotation.y += 0.01;
        }
      });
      
      controls.update();
      renderer.render(scene, camera);
    };
    
    animate();
    
    // Add HTML overlays to the DOM
    objectsInScene.forEach(obj => {
      document.body.appendChild(obj.label);
    });
    
    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    
    window.addEventListener('resize', handleResize);
    
    // Cleanup function
    return () => {
      window.removeEventListener('click', handleMouseClick);
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', handleResize);
      
      objectsInScene.forEach(obj => {
        if (obj.label.parentNode) {
          obj.label.parentNode.removeChild(obj.label);
        }
      });
      
      stopCameraStream();
      
      if (mountRef.current && mountRef.current.contains(renderer.domElement)) {
        mountRef.current.removeChild(renderer.domElement);
      }
    };
  }, []);
  
  // Format date string nicely
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };
  
  // Get status indicator color
  const getStatusClass = (status) => {
    switch(status) {
      case "Critical": return "status-critical";
      case "Warning": return "status-warning";
      case "Offline": return "status-offline";
      default: return "status-active";
    }
  };
  
  // Render enhanced card-style data panel
  const renderDataPanel = () => {
    if (!selectedObject || !showDataPanel) return null;
    
    const { data } = selectedObject;
    const statusClass = getStatusClass(data.status);
    
    return (
      <div className="data-panel card-ui">
        <div className="panel-header" style={{ backgroundColor: `#${data.color.toString(16)}` }}>
          <div className="header-content">
            <div className={`status-indicator ${statusClass}`}></div>
            <h3>{data.name}</h3>
          </div>
          <button className="close-button" onClick={() => setShowDataPanel(false)}></button>
        </div>
        
        <div className="panel-content">
          <div className="data-grid">
            <div className="data-section location-section">
              <h4>Location</h4>
              <div className="data-grid-row">
                <div className="data-grid-cell">
                  <span className="data-label">X:</span>
                  <span className="data-value">{data.position.x.toFixed(2)} m</span>
                </div>
                <div className="data-grid-cell">
                  <span className="data-label">Y:</span>
                  <span className="data-value">{data.position.y.toFixed(2)} m</span>
                </div>
                <div className="data-grid-cell">
                  <span className="data-label">Z:</span>
                  <span className="data-value">{data.position.z.toFixed(2)} m</span>
                </div>
              </div>
            </div>
            
            <div className="data-section properties-section">
              <h4>Properties</h4>
              <div className="data-grid-row">
                <div className="data-grid-cell">
                  <span className="data-label">Type:</span>
                  <span className="data-value">{data.type}</span>
                </div>
                <div className="data-grid-cell">
                  <span className="data-label">Status:</span>
                  <span className={`data-value ${statusClass}`}>{data.status}</span>
                </div>
              </div>
              <div className="data-grid-row">
                <div className="data-grid-cell full-width">
                  <span className="data-label">Last Updated:</span>
                  <span className="data-value">{formatDate(data.lastUpdated)}</span>
                </div>
              </div>
              <div className="data-grid-row">
                <div className="data-grid-cell full-width">
                  <span className="data-label">Description:</span>
                  <span className="data-value description">{data.description}</span>
                </div>
              </div>
            </div>
            
            <div className="data-section readings-section">
              <h4>Recent Readings</h4>
              <div className="mini-chart">
                {data.readings.map((value, index) => (
                  <div 
                    key={index} 
                    className="chart-bar"
                    style={{ 
                      height: `${value}%`,
                      backgroundColor: index === data.readings.length - 1 ? 
                        `#${data.color.toString(16)}` : '#3498db'
                    }}
                  >
                    <span className="tooltip">{value}</span>
                  </div>
                ))}
              </div>
              <div className="chart-legend">
                <span>Past</span>
                <span>Present</span>
              </div>
            </div>
          </div>
          
          <div className="action-buttons">
            <button className="action-button">
              <i className="icon-details"></i>
              <span>Details</span>
            </button>
            <button className="action-button">
              <i className="icon-history"></i>
              <span>History</span>
            </button>
            <button className="action-button">
              <i className="icon-share"></i>
              <span>Share</span>
            </button>
          </div>
        </div>
      </div>
    );
  };
  
  // Render minimap with improved design
  const renderMinimap = () => {
    return (
      <div className="minimap card-ui">
        <div className="minimap-header">
          <span>Map View</span>
          <div className="minimap-controls">
            <button className="minimap-button zoom-in">+</button>
            <button className="minimap-button zoom-out">-</button>
          </div>
        </div>
        <div className="minimap-content">
          <div className="grid-lines">
            <div className="grid-line horizontal"></div>
            <div className="grid-line horizontal"></div>
            <div className="grid-line horizontal"></div>
            <div className="grid-line vertical"></div>
            <div className="grid-line vertical"></div>
            <div className="grid-line vertical"></div>
          </div>
          {objects.map(obj => {
            const statusClass = getStatusClass(obj.data.status);
            return (
              <div 
                key={obj.data.id}
                className={`map-marker ${selectedObject?.data.id === obj.data.id ? 'selected' : ''} ${statusClass}`}
                style={{
                  backgroundColor: `#${obj.data.color.toString(16)}`,
                  left: `${(obj.data.position.x + 10) / 20 * 100}%`,
                  top: `${(obj.data.position.z + 10) / 20 * 100}%`,
                }}
                onClick={() => {
                  setSelectedObject(obj);
                  setShowDataPanel(true);
                }}
              >
                <span className="marker-tooltip">{obj.data.name}</span>
              </div>
            );
          })}
          <div className="compass">
            <div className="compass-n">N</div>
            <div className="compass-s">S</div>
            <div className="compass-e">E</div>
            <div className="compass-w">W</div>
          </div>
        </div>
      </div>
    );
  };
  
  // Render filter panel
  const renderFilterPanel = () => {
    return (
      <div className="filter-panel card-ui">
        <div className="filter-header">
          <span>Filters</span>
        </div>
        <div className="filter-content">
          <div className="filter-group">
            <label className="filter-label">Status</label>
            <div className="filter-options">
              <button className="filter-option active">All</button>
              <button className="filter-option status-active">Active</button>
              <button className="filter-option status-warning">Warning</button>
              <button className="filter-option status-critical">Critical</button>
              <button className="filter-option status-offline">Offline</button>
            </div>
          </div>
          <div className="filter-group">
            <label className="filter-label">Type</label>
            <div className="filter-options">
              <button className="filter-option active">All</button>
              <button className="filter-option">Temperature</button>
              <button className="filter-option">Humidity</button>
              <button className="filter-option">Pressure</button>
              <button className="filter-option">More...</button>
            </div>
          </div>
        </div>
      </div>
    );
  };
  
  return (
    <div className="ar-container">
      {/* Camera video feed for AR */}
      {cameraActive && (
        <video 
          ref={videoRef}
          className="camera-feed"
          autoPlay
          playsInline
        />
      )}
      
      {/* Three.js scene */}
      <div ref={mountRef} className="ar-scene"></div>
      
      {/* UI Components */}
      <div className="ui-layer">
        {renderDataPanel()}
        {renderMinimap()}
        {renderFilterPanel()}
        
        {/* Object count and status bar */}
        <div className="status-bar card-ui">
          <div className="status-content">
            <div className="object-count">
              <span className="count-number">{objects.length}</span>
              <span className="count-label">Objects Detected</span>
            </div>
            <div className="status-indicators">
              <div className="status-group">
                <div className="status-dot status-active"></div>
                <span>Active</span>
              </div>
              <div className="status-group">
                <div className="status-dot status-warning"></div>
                <span>Warning</span>
              </div>
              <div className="status-group">
                <div className="status-dot status-critical"></div>
                <span>Critical</span>
              </div>
            </div>
          </div>
        </div>
        
        {/* Main controls menu */}
        <div className="action-menu card-ui">
          <button 
            className={`menu-button camera-button ${cameraActive ? 'active' : ''}`}
            onClick={toggleCamera}
          >
            <i className="icon-camera"></i>
          </button>
          <button className="menu-button add-button">
            <i className="icon-add"></i>
          </button>
          <button className="menu-button search-button">
            <i className="icon-search"></i>
          </button>
          <button className="menu-button voice-button">
            <i className="icon-voice"></i>
          </button>
          <button className="menu-button settings-button">
            <i className="icon-settings"></i>
          </button>
        </div>
        
        {/* Contextual help */}
        <div className="instructions card-ui">
          <p>Click on objects to view details. Drag to rotate camera. Double-click to zoom.</p>
          <button className="dismiss-button"></button>
        </div>
      </div>
    </div>
  );
};

export default ImprovedARVisualizer;
